import * as React from 'react';
import { BottomNavigation, Text } from 'react-native-paper';
import {ActivitiesScreen} from "../.."

const LeaderboardScreen = () => <Text>Leaderboard</Text>;

const ProfileScreen = () => <Text>Profile</Text>;

const SettingsScreen = () => <Text>Settings</Text>;


class BottomNavigationBar extends React.Component {
  state = {
    index: 0,
    routes: [
      { key: 'activities', title: 'Activities', icon: 'calendar-today' },
      { key: 'leaderboard', title: 'Leaderboard', icon: 'trophy' },
      { key: 'profile', title: 'Profile', icon: 'account' },
      { key: 'settings', title: 'Settings', icon: 'settings' },
    ],
  };

  _handleIndexChange = index => this.setState({ index });

  _renderScene = BottomNavigation.SceneMap({
    activities: ActivitiesScreen,
    leaderboard: LeaderboardScreen,
    profile: ProfileScreen,
    settings: SettingsScreen
  });

  render() {
    return (
      <BottomNavigation
      shifting={false}
        navigationState={this.state}
        onIndexChange={this._handleIndexChange}
        renderScene={this._renderScene}
      />
    );
  }
};

export default BottomNavigationBar;
